import React, { Component } from "react";
import tokenLogo from "../token-logo.png";
import ethLogo from "../eth-logo.png";
import BuySecond from "./BuySecond.js";

class NewForm extends Component {
  constructor(props) {
    super(props);
    this.state = {
      input: "0",
      output: "0",
      price: "1",
    };
  }

  render() {
    return (
      <>
        <form
          className="mb-3"
          onSubmit={(event) => {
            event.preventDefault();
            let etherAmount;
            etherAmount = this.state.input.toString();

            const price = this.state.price;
            etherAmount = window.web3.utils.toWei(etherAmount, "Ether");
            if (this.props.buyorsell === "buy") {
              this.props.placeBuyOrder(price, etherAmount);
            } else {
              this.props.placeSellOrder(price, etherAmount);
            }
          }}
        >
          <div>
            <label className="float-left">
              <b>Input</b>
            </label>
            <span className="float-right text-muted"></span>
          </div>
          <div className="input-group mb-4">
            <div className="input-group-append">
              <div className="input-group-text">
                <img src={ethLogo} height="32" alt="" />
                &nbsp;&nbsp;&nbsp; ETH
              </div>
            </div>
            <input
              type="text"
              onChange={(event) => {
                const etherAmount = event.target.value.toString();
                console.log(etherAmount);

                this.setState({
                  input: etherAmount,
                  output: etherAmount,
                });
              }}
              ref={(input) => {
                this.input = input;
              }}
              className="form-control form-control-lg"
              placeholder="0"
              required
            />
          </div>
          <>
            <>
              <div>
                <label className="float-left">
                  <b>price</b>
                </label>
              </div>
              <div className="input-group mb-4">
                <input
                  type="text"
                  onChange={(e) => {
                    const newprice = e.target.value.toString();
                    console.log(newprice);
                    this.setState({
                      price: newprice,
                    });
                  }}
                  ref={(price) => {
                    this.price = price;
                  }}
                  className="form-control form-control-lg"
                  placeholder="10"
                />
              </div>
            </>
          </>

          <button type="submit" className="btn btn-primary btn-block btn-lg">
            Swap
          </button>
        </form>
        <></>
      </>
    );
  }
}

export default NewForm;
